/**
 * An object with all {@link DurationUnit} keys set to `0`.
 *
 * @category Constants
 */
export const zeroDuration = {
    years: 0,
    months: 0,
    weeks: 0,
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
    milliseconds: 0,
};
/**
 * An object with all {@link DurationUnit} keys set to `0`. Alias for {@link zeroDuration}.
 *
 * @category Constants
 */
export const emptyDuration = zeroDuration;
