/**
 * Indicates whether the global `Buffer` constructor exists or not. This only exists in Node.js.
 *
 * @category Internal
 */
export const doesBufferExist = typeof Buffer !== 'undefined';
/**
 * Is it Buffer?
 *
 * @category Internal
 */
export const isBuffer = doesBufferExist
    ? Buffer.isBuffer.bind(Buffer)
    : /** Return false every time if Buffer unsupported */
        function isBuffer(obj) {
            return false;
        };
/**
 * Clone Buffer.
 *
 * @category Internal
 */
export const cloneBuffer = doesBufferExist
    ? Buffer.from.bind(Buffer)
    : /** Return argument use if Buffer unsupported */
        function cloneBuffer(value) {
            return value;
        };
