import { copy } from './clone.js';
import { getKeys, getValue, isCollection, setValue } from './collection.js';
import { detectType } from './detector.js';
/**
 * Recursively copy
 *
 * @category Internal
 */
function recursiveCopy(value, clone, references, visited, customizer) {
    const valueType = detectType(value);
    const copiedValue = copy(value, valueType);
    // return if not a collection value
    if (!isCollection(valueType)) {
        return copiedValue;
    }
    const keys = getKeys(value, valueType);
    // walk within collection with iterator
    for (const collectionKey of keys) {
        const collectionValue = getValue(value, collectionKey, valueType);
        if (visited.has(collectionValue)) {
            // for [Circular]
            setValue(clone, collectionKey, references.get(collectionValue), valueType);
        }
        else {
            const collectionValueType = detectType(collectionValue);
            const copiedCollectionValue = copy(collectionValue, collectionValueType);
            // save reference if value is collection
            if (isCollection(collectionValueType)) {
                references.set(collectionValue, copiedCollectionValue);
                visited.add(collectionValue);
            }
            setValue(clone, collectionKey, recursiveCopy(collectionValue, copiedCollectionValue, references, visited, customizer), valueType);
        }
    }
    return clone;
}
/**
 * Deep copy value
 *
 * @category Main
 */
export function deepCopy(value, options) {
    const { customizer = null } = options || {};
    const valueType = detectType(value);
    if (!isCollection(valueType)) {
        return copy(value, valueType, customizer);
    }
    const copiedValue = copy(value, valueType, customizer);
    const references = new WeakMap([
        [
            value,
            copiedValue,
        ],
    ]);
    const visited = new WeakSet([value]);
    return recursiveCopy(value, copiedValue, references, visited, customizer);
}
